import React, { useState } from 'react';
import GameBoard from './components/GameBoard';
import MainScreen from './components/MainScreen'; // Assuming you have a MainScreen component

const App = () => {
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [coins, setCoins] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);

  const handleGameOver = () => {
    setIsGameOver(true);
  };

  const restartGame = () => {
    setScore(0);
    setLives(3);
    setCoins(0);
    setIsGameOver(false);
  };

  return (
    <div>
      {isGameOver ? (
        <MainScreen onRestart={restartGame} />
      ) : (
        <GameBoard
          setScore={setScore}
          setLives={setLives}
          setCoins={setCoins}
          score={score}
          lives={lives}
          coins={coins}
          onGameOver={handleGameOver}
        />
      )}
    </div>
  );
};

export default App;
