// src/components/GameBoard.js

import React, { useState, useEffect, useRef } from 'react';
import './GameBoard.css';
import Duckling from './Duckling';
import MCQModal from './MCQModal';
import { questions } from '../data/questions';
import bonusImage from '../assets/bonus.png'; // Ensure correct path

const GameBoard = ({
  setScore,
  setLives,
  setCoins,
  score,
  lives,
  coins,
}) => {
  const gameBoardRef = useRef(null);
  const [leftPosition, setLeftPosition] = useState(50);
  const [isJumping, setIsJumping] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showMCQ, setShowMCQ] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answeredBlocks, setAnsweredBlocks] = useState(new Set());
  const [cameraX, setCameraX] = useState(0);

  const triggeredRef = useRef(new Set()); // To prevent multiple triggers

  const forwardSpeed = 2; // pixels per interval
  const jumpHeight = 150;
  const jumpDuration = 600;
  const blockWidth = 100;
  const ducklingWidth = 50;
  const ducklingHeight = 50;
  const threshold = 10; // pixels

  const BLOCK_TYPES = {
    BLUE: 'blue',
    RED: 'red',
    QUESTION: 'question',
    USED_QUESTION: 'used-question',
  };

  // Helper function to generate blocks ensuring no two reds in a row
  const generateBlock = (lastBlockType) => {
    const randomValue = Math.random();

    // Decide the Y position based on block type
    let yPosition = 0; // Ground level by default

    if (lastBlockType === BLOCK_TYPES.RED) {
      // Ensure no two reds in a row
      return {
        type: BLOCK_TYPES.BLUE,
        x: 0,
        y: yPosition,
        width: blockWidth,
        height: 50,
      };
    } else if (randomValue < 0.1) { // 10% chance for question block
      yPosition = jumpHeight; // Floating block
      return {
        type: BLOCK_TYPES.QUESTION,
        x: 0,
        y: yPosition,
        width: blockWidth,
        height: 50,
        interacted: false, // To track interaction
      };
    } else {
      return {
        type: Math.random() < 0.3 ? BLOCK_TYPES.RED : BLOCK_TYPES.BLUE,
        x: 0,
        y: yPosition,
        width: blockWidth,
        height: 50,
      };
    }
  };

  // 1. Initial Block Generation
  const [blocks, setBlocks] = useState(() => {
    const initialBlocks = [];

    for (let i = 0; i < 15; i++) {
      if (i === 0) {
        // First block is always blue on the ground
        initialBlocks.push({
          type: BLOCK_TYPES.BLUE,
          x: i * blockWidth,
          y: 0, // Ground level
          width: blockWidth,
          height: 50,
        });
      } else {
        // Use helper function to ensure no two reds in a row
        const lastBlockType = initialBlocks[i - 1].type;
        const newBlock = generateBlock(lastBlockType);
        newBlock.x = i * blockWidth;
        initialBlocks.push(newBlock);
      }
    }

    return initialBlocks;
  });

  // 2. Handle Key Press for Jumping
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.code === 'Space' && !isJumping && !isPaused) {
        jump();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isJumping, isPaused]);

  // 3. Game Loop Effect
  useEffect(() => {
    if (!isPaused) {
      const gameLoop = setInterval(() => {
        setLeftPosition((prevLeft) => {
          const potentialNewPos = prevLeft + forwardSpeed;
          const gameWidth = gameBoardRef.current.clientWidth;

          // Update cameraX if the duck reaches the center
          if (potentialNewPos - cameraX >= gameWidth / 2) {
            setCameraX(potentialNewPos - gameWidth / 2);

            // Dynamically add new blocks if nearing the end
            const lastBlock = blocks[blocks.length - 1];
            if (lastBlock.x - cameraX < gameWidth + blockWidth) {
              setBlocks((prevBlocks) => {
                const lastBlockType = prevBlocks[prevBlocks.length - 1]?.type || BLOCK_TYPES.BLUE;
                const newBlock = generateBlock(lastBlockType);
                newBlock.x = prevBlocks[prevBlocks.length - 1].x + blockWidth;
                return [...prevBlocks, newBlock];
              });
            }
          }

          const duckLeft = potentialNewPos;
          const duckRight = duckLeft + ducklingWidth;

          // Determine Duckling's Vertical Position
          const duckBottom = gameBoardRef.current.clientHeight - ducklingHeight - 50; // 50px platform height
          const duckTop = isJumping ? duckBottom - jumpHeight : duckBottom;

          // Iterate through all blocks to check for collision
          for (let i = 0; i < blocks.length; i++) {
            const block = blocks[i];
            const blockLeft = block.x;
            const blockRight = block.x + block.width;
            const blockTop = block.y === 0
              ? gameBoardRef.current.clientHeight - block.height - 50
              : gameBoardRef.current.clientHeight - block.height - 50 - block.y;

            // Check horizontal overlap
            const isHorizontalOverlap = duckRight > blockLeft + threshold && duckLeft < blockRight - threshold;

            // Check vertical overlap based on block type
            let isVerticalOverlap = false;
            if (block.y === 0) {
              // Ground block collision
              isVerticalOverlap = !isJumping && duckBottom <= blockTop + threshold;
            } else {
              // Floating block collision
              isVerticalOverlap = isJumping && duckTop <= blockTop + threshold;
            }

            if (isHorizontalOverlap && isVerticalOverlap) {
              if (block.type === BLOCK_TYPES.RED && !answeredBlocks.has(i)) {
                triggerRedBlockEvent(i);
                return prevLeft; // Prevent moving into the red block
              }

              if (block.type === BLOCK_TYPES.QUESTION && !block.interacted) {
                if (!triggeredRef.current.has(i)) { // Prevent multiple triggers
                  triggerQuestionBlockEvent(i);
                  triggeredRef.current.add(i);
                }
                return prevLeft; // Prevent moving into the question block
              }
            }
          }

          // Ensure the duck doesn't move beyond the current blocks
          const maxLeft = blocks[blocks.length - 1].x + blockWidth - ducklingWidth;
          return Math.min(potentialNewPos, maxLeft);
        });
      }, 20);

      return () => clearInterval(gameLoop);
    }
  }, [isPaused, blocks, answeredBlocks, ducklingWidth, cameraX]);

  const jump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setTimeout(() => setIsJumping(false), jumpDuration);
    }
  };

  const triggerQuestionBlockEvent = (blockIndex) => {
    setIsPaused(true);
    setShowMCQ(true);
    setCurrentQuestion(questions[Math.floor(Math.random() * questions.length)]);

    // Mark block as interacted
    setBlocks((prevBlocks) => {
      const updatedBlocks = [...prevBlocks];
      updatedBlocks[blockIndex] = {
        ...updatedBlocks[blockIndex],
        interacted: true,
        type: BLOCK_TYPES.USED_QUESTION,
      };
      return updatedBlocks;
    });
  };

  const triggerRedBlockEvent = (blockIndex) => {
    // Existing logic for red blocks
    setLives((prevLives) => {
      const newLives = prevLives - 1;
      if (newLives <= 0) {
        alert('Game Over!');
        resetGame(); // Reset the game entirely
        return 3; // Reset lives to initial value after game over
      }
      return newLives;
    });

    // Prevent moving into the red block
    setLeftPosition(50);
    setCameraX(0);

    // Optionally, reset answered blocks to allow revisiting questions
    setAnsweredBlocks(new Set());

    setIsPaused(false);
  };

  // 4. Reset Game Function
  const resetGame = () => {
    setLeftPosition(50);
    setAnsweredBlocks(new Set());
    setCameraX(0);
    setIsPaused(false);
    setIsJumping(false);
    setBlocks(() => {
      const initialBlocks = [];

      for (let i = 0; i < 15; i++) {
        if (i === 0) {
          // First block is always blue on the ground
          initialBlocks.push({
            type: BLOCK_TYPES.BLUE,
            x: i * blockWidth,
            y: 0, // Ground level
            width: blockWidth,
            height: 50,
          });
        } else {
          // Use helper function to ensure no two reds in a row
          const lastBlockType = initialBlocks[i - 1].type;
          const newBlock = generateBlock(lastBlockType);
          newBlock.x = i * blockWidth;
          initialBlocks.push(newBlock);
        }
      }

      return initialBlocks;
    });
    setLives(3); // Reset lives to initial value
    setScore(0);
    setCoins(0);
  };

  // 5. Handle Answer Function
  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setCoins((prevCoins) => prevCoins + 10); // Reward 10 extra coins
      setScore((prevScore) => prevScore + 10);
      // Optionally, provide feedback
    } else {
      // No penalty as per requirements
      // Optionally, provide feedback
    }

    setIsPaused(false); // Resume the game
    setShowMCQ(false); // Hide the MCQ modal
    triggeredRef.current.clear(); // Allow future interactions
  };

  return (
    <div className="game-board" ref={gameBoardRef}>
      <div className="platform">
        {blocks.map((block, index) => (
          <div
            key={index}
            className={`block ${block.type} ${block.y === jumpHeight ? 'floating' : ''}`}
            style={{
              left: `${block.x - cameraX}px`,
              width: `${block.width}px`,
              height: `${block.height}px`,
            }}
          />
        ))}
      </div>
      <Duckling
        className={isJumping ? 'jumping' : ''}
        style={{
          left: `${leftPosition - cameraX}px`,
          transform: isJumping ? `translateY(-${jumpHeight}px)` : 'translateY(0)',
          transition: `transform ${jumpDuration}ms`,
        }}
      />
      {showMCQ && currentQuestion && (
        <MCQModal questionData={currentQuestion} onAnswer={handleAnswer} />
      )}
    </div>
  );
};

export default GameBoard;
