// src/components/MCQModal.js

import React from 'react';
import './MCQModal.css';

const MCQModal = ({ questionData, onAnswer }) => {
  const { question, options, correctAnswer, isBonus } = questionData;

  const handleOptionClick = (option) => {
    const isCorrect = option === correctAnswer;
    onAnswer(isCorrect);
  };

  return (
    <div className="MCQModal">
      <h2>{isBonus ? 'Bonus Question!' : 'Question'}</h2>
      <p>{question}</p>
      <div className="options">
        {options.map((option, idx) => (
          <button key={idx} onClick={() => handleOptionClick(option)}>
            {option}
          </button>
        ))}
      </div>
      {!isBonus && (
        <p style={{ marginTop: '10px', fontStyle: 'italic' }}>
          Incorrect answers will cost you a life!
        </p>
      )}
    </div>
  );
};

export default MCQModal;
