// src/App.js

import React, { useState } from 'react';
import GameHeader from './components/GameHeader';
import GameBoard from './components/GameBoard';
import Sidebar from './components/Sidebar';
import GameFooter from './components/GameFooter';
import MainScreen from './components/MainScreen'; // New component for Game Over and Welcome Screen
import './styles/App.css';

const App = () => {
  // State variables
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [coins, setCoins] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);

  // Handler to start the game
  const startGame = () => {
    setGameStarted(true);
    setIsGameOver(false);
    setScore(0);
    setLives(3);
    setCoins(0);
    console.log('Game Started!');
  };

  // Handler when the game is over
  const handleGameOver = () => {
    setIsGameOver(true);
    setGameStarted(false);
    console.log('Game Over!');
  };

  // Handler to restart the game
  const restartGame = () => {
    setScore(0);
    setLives(3);
    setCoins(0);
    setIsGameOver(false);
    setGameStarted(true);
    console.log('Game Restarted!');
  };

  return (
    <div className="app-container">
      {/* Game Header */}
      <GameHeader title="Duckling Game" score={score} lives={lives} coins={coins} />

      <div className="content">
        {/* Sidebar */}
        <Sidebar />

        {/* Main Content Area */}
        {isGameOver ? (
          // Show MainScreen when the game is over
          <MainScreen
            message="Game Over!"
            onRestart={restartGame}
            score={score}
            coins={coins}
          />
        ) : gameStarted ? (
          // Show GameBoard when the game is active
          <GameBoard
            setScore={setScore}
            setLives={setLives}
            setCoins={setCoins}
            score={score}
            lives={lives}
            coins={coins}
            onGameOver={handleGameOver}
          />
        ) : (
          // Show welcome screen when the game hasn't started
          <MainScreen
            message="Welcome! Click 'Start Game' to begin."
            onRestart={startGame}
          />
        )}
      </div>

      {/* Game Footer */}
      {!gameStarted && (
        <GameFooter onStart={startGame} />
      )}
    </div>
  );
};

export default App;
