// src/components/GameBoard.js

import React, { useState, useEffect, useRef } from 'react';
import './GameBoard.css';
import Duckling from './Duckling';
import MCQModal from './MCQModal';
import { questions } from '../data/questions';

const GameBoard = ({
  setScore,
  setLives,
  setCoins,
  score,
  lives,
  coins,
}) => {
  const gameBoardRef = useRef(null);
  const [platformOffset, setPlatformOffset] = useState(0); // Platform movement offset
  const [isJumping, setIsJumping] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showMCQ, setShowMCQ] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answeredBlocks, setAnsweredBlocks] = useState(new Set());

  const forwardSpeed = 1; // Reduced platform movement speed (pixels per frame)
  const jumpHeight = 150; // Duckling jump height
  const jumpDuration = 600; // Duckling jump duration
  const blockWidth = 80; // Width of each block
  const totalBlocks = 50; // Increased total number of blocks

  const ducklingWidth = 50; // Duckling width
  const ducklingHeight = 50; // Duckling height

  // Define blocks with their types and positions
  const blocks = Array.from({ length: totalBlocks }).map((_, index) => ({
    type: index % 5 === 0 ? 'red' : 'blue', // Red blocks every 5 blocks
    x: index * blockWidth, // Initial x-position of the block
    width: blockWidth,
    height: 50,
  }));

  // Game loop using requestAnimationFrame
  const gameLoopRef = useRef();

  const animate = () => {
    if (!isPaused) {
      // Move the platform towards the duckling
      setPlatformOffset((prevOffset) => prevOffset - forwardSpeed);

      // Collision detection
      const ducklingLeft = gameBoardRef.current.clientWidth / 2 - ducklingWidth / 2; // Duckling's fixed position
      const ducklingRight = ducklingLeft + ducklingWidth;

      for (let i = 0; i < blocks.length; i++) {
        const block = blocks[i];
        const blockLeft = block.x + platformOffset; // Adjusted block position
        const blockRight = blockLeft + block.width;

        // Check if duckling overlaps with this block
        const isOverlapping =
          ducklingRight > blockLeft && ducklingLeft < blockRight;

        if (
          isOverlapping &&
          block.type === 'red' &&
          !answeredBlocks.has(i)
        ) {
          // Pause the game immediately
          setIsPaused(true);
          triggerMCQ(i);
          return; // Exit the loop to prevent further updates
        }
      }

      // Check if the platform has ended
      const platformEnd = blocks[blocks.length - 1].x + platformOffset + blockWidth;
      if (platformEnd < gameBoardRef.current.clientWidth / 2) {
        // Platform has ended
        setIsPaused(true);
        alert('Congratulations! You reached the end of the platform!');
        resetGame();
      }
    }

    // Request the next frame
    gameLoopRef.current = requestAnimationFrame(animate);
  };

  useEffect(() => {
    // Start the game loop
    gameLoopRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(gameLoopRef.current);
  }, [isPaused, blocks, answeredBlocks]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.code === 'Space' && !isJumping && !isPaused) {
        jump();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isJumping, isPaused]);

  const jump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setTimeout(() => setIsJumping(false), jumpDuration);
    }
  };

  const triggerMCQ = (blockIndex) => {
    setShowMCQ(true);
    setCurrentQuestion(questions[Math.floor(Math.random() * questions.length)]);
    setAnsweredBlocks((prev) => new Set(prev).add(blockIndex));
  };

  const resetGame = () => {
    setPlatformOffset(0); // Reset platform position
    setAnsweredBlocks(new Set());
    setIsPaused(false);
    setIsJumping(false);
  };

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setCoins((prevCoins) => prevCoins + 1);
      setScore((prevScore) => prevScore + 10);
    } else {
      setLives((prevLives) => prevLives - 1);
      if (lives - 1 <= 0) {
        // Game over logic here
        alert('Game Over!');
        // Optionally, reset the game or navigate to Game Over screen
      } else {
        resetGame(); // Reset the game state including answeredBlocks
      }
    }
    setIsPaused(false); // Resume the game
    setShowMCQ(false); // Hide the MCQ modal
  };

  return (
    <div className="game-board" ref={gameBoardRef}>
      <div
        className="platform"
        style={{
          transform: `translateX(${platformOffset}px)`, // Move the platform
        }}
      >
        {blocks.map((block, index) => (
          <div
            key={index}
            className={`block ${block.type}`}
            style={{
              left: `${block.x}px`,
              width: `${block.width}px`,
              height: `${block.height}px`,
            }}
          />
        ))}
      </div>
      <Duckling
        className={isJumping ? 'jumping' : ''}
        style={{
          left: `calc(50% - ${ducklingWidth / 2}px)`, // Duckling stays in the center
          transform: isJumping ? `translateY(-${jumpHeight}px)` : 'translateY(0)',
          transition: `transform ${jumpDuration}ms`,
        }}
      />
      {showMCQ && currentQuestion && (
        <MCQModal questionData={currentQuestion} onAnswer={handleAnswer} />
      )}
    </div>
  );
};

export default GameBoard;
