// src/App.js

import React, { useState } from 'react';
import GameHeader from './components/GameHeader';
import GameBoard from './components/GameBoard';
import Sidebar from './components/Sidebar';
import GameFooter from './components/GameFooter';
import GameOver from './components/GameOver'; // New Import
import './styles/App.css';

const App = () => {
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [coins, setCoins] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false); // New State

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false); // Reset game over state
    setScore(0);
    setLives(3);
    setCoins(0);
    // Initialize or reset other game states here
    console.log('Game Started!');
  };

  const handleGameOver = () => {
    setGameOver(true);
    setGameStarted(false);
    console.log('Game Over!');
  };

  const handleRestart = () => {
    startGame();
  };

  return (
    <div className="app-container">
      <GameHeader title="Duckling Game" score={score} lives={lives} coins={coins} />
      <div className="content">
        <Sidebar />
        {gameStarted ? (
          <GameBoard
            setScore={setScore}
            setLives={setLives}
            setCoins={setCoins}
            score={score}
            lives={lives}
            coins={coins}
            onGameOver={handleGameOver} // Pass the callback
          />
        ) : gameOver ? (
          <GameOver score={score} onRestart={handleRestart} />
        ) : (
          <div style={welcomeStyle}>
            <h1>Welcome to the Duckling Game!</h1>
            <button onClick={startGame} className="start-button">
              Start Game
            </button>
          </div>
        )}
      </div>
      {!gameStarted && !gameOver && <GameFooter onStart={startGame} />}
    </div>
  );
};

const welcomeStyle = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column', // Center button vertically
  justifyContent: 'center',
  alignItems: 'center',
  fontSize: '24px',
  color: '#555',
};

export default App;
