// src/entities/Platform.js
import Matter from 'matter-js';
import React from 'react';

const Platform = (x, y, width, height, isPassable) => {
  const platform = {
    body: Matter.Bodies.rectangle(x, y, width, height, {
      isStatic: true,
      label: isPassable ? 'passable' : 'filled',
      friction: 0.5,
      restitution: 0.0,
    }),
    isPassable,
    renderer: <PlatformRenderer width={width} height={height} isPassable={isPassable} />,
  };
  return platform;
};

const PlatformRenderer = ({ width, height, isPassable }) => {
  const style = {
    position: 'absolute',
    width: `${width}px`,
    height: `${height}px`,
    backgroundColor: isPassable ? 'blue' : 'gray',
    transform: 'translate(-50%, -50%)',
  };

  return <div style={style} />;
};

export default Platform;
