import React, { useState, useEffect, useRef } from 'react';
import './GameBoard.css';
import Duckling from './Duckling';
import MCQModal from './MCQModal';
import { questions } from '../data/questions'; // Ensure this path is correct

const GameBoard = ({
  setScore,
  setLives,
  setCoins,
  score,
  lives,
  coins,
  onGameOver, // Callback to handle game over
}) => {
  const gameBoardRef = useRef(null);

  // Duckling position states
  const [leftPosition, setLeftPosition] = useState(50); // Initial left position in px
  const [isJumping, setIsJumping] = useState(false);
  const [facingLeft, setFacingLeft] = useState(false); // To handle orientation
  const [isPaused, setIsPaused] = useState(false); // New state to control pause
  const forwardSpeed = 2; // Continuous forward movement speed
  const moveSpeed = 5; // Pixels to move per interval
  const jumpDuration = 600; // Total jump duration in ms

  // MCQ Modal states
  const [showMCQ, setShowMCQ] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [duckOnRed, setDuckOnRed] = useState(false);

  // Key press state
  const keysPressed = useRef({});

  // Define block properties
  const blockWidth = 80; // As per .block width in CSS
  const platformY = 50; // Platform height in px

  // Total blocks and block positions
  const totalBlocks = 20; // Number of blocks on the platform
  const blocks = Array.from({ length: totalBlocks }).map((_, index) => ({
    type: [4, 9, 14, 19].includes(index) ? 'red' : 'blue',
    x: index * blockWidth,
    y: 0, // Platforms are at y=0 relative to game board
    width: blockWidth,
    height: 50, // As per .platform height in CSS
  }));

  useEffect(() => {
    // Key down handler
    const handleKeyDown = (e) => {
      keysPressed.current[e.code] = true;
    };

    // Key up handler
    const handleKeyUp = (e) => {
      keysPressed.current[e.code] = false;
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Game loop
    const gameLoop = setInterval(() => {
      if (!isPaused) {
        // Continuous Forward Movement
        setLeftPosition((prev) => {
          const newPos = prev + forwardSpeed;
          const maxLeft = gameBoardRef.current.clientWidth - 50; // 50 is the duckling width
          return Math.min(newPos, maxLeft);
        });

        // Optional: Handle Left and Right Movements if desired
        if (keysPressed.current['ArrowLeft'] || keysPressed.current['KeyA']) {
          setLeftPosition((prev) => Math.max(prev - moveSpeed, 0));
          setFacingLeft(true);
        }

        if (keysPressed.current['ArrowRight'] || keysPressed.current['KeyD']) {
          setLeftPosition((prev) =>
            Math.min(prev + moveSpeed, gameBoardRef.current.clientWidth - 50)
          );
          setFacingLeft(false);
        }

        // Jump
        if (
          (keysPressed.current['Space'] || keysPressed.current['Spacebar']) &&
          !isJumping
        ) {
          setIsJumping(true);
          // Automatically reset jumping state after jumpDuration
          setTimeout(() => {
            setIsJumping(false);
          }, jumpDuration);
        }

        // Check for landing on red block only when not jumping
        if (!isJumping) {
          const duckCenter = leftPosition + 25; // Duck's center x position (50px width)
          const duckBlockIndex = Math.floor(duckCenter / blockWidth);
          if (
            duckBlockIndex >= 0 &&
            duckBlockIndex < totalBlocks &&
            blocks[duckBlockIndex].type === 'red' &&
            !duckOnRed
          ) {
            triggerMCQ();
          }
        }
      }
    }, 20); // Adjust game loop speed as needed

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      clearInterval(gameLoop);
    };
  }, [isJumping, blocks, duckOnRed, isPaused]); // Added isPaused to dependency array

  const triggerMCQ = () => {
    // Select a random question
    const randomIndex = Math.floor(Math.random() * questions.length);
    setCurrentQuestion(questions[randomIndex]);
    setShowMCQ(true);
    setIsPaused(true); // Pause the game when MCQ is triggered
    setDuckOnRed(true); // Prevent multiple triggers
  };

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setCoins((prevCoins) => prevCoins + 1);
      setScore((prevScore) => prevScore + 10); // Increment score as desired
    } else {
      setLives((prevLives) => {
        const newLives = prevLives - 1;
        if (newLives <= 0) {
          onGameOver(); // Trigger game over
        }
        return newLives;
      });
      resetDuckling();
    }
    setShowMCQ(false);
    setDuckOnRed(false);
    setIsPaused(false); // Resume movement after answering
  };

  const resetDuckling = () => {
    setLeftPosition(50); // Reset to starting position
    // Optionally, reset other states or add effects
  };

  return (
    <div className="game-board" ref={gameBoardRef}>
      {/* Platforms */}
      <div className="platform">
        {blocks.map((block, index) => (
          <div
            key={index}
            className={`block ${block.type}`}
            style={{
              left: `${block.x}px`,
              // Other styles are handled by CSS
            }}
          ></div>
        ))}
      </div>
      {/* Duckling */}
      <Duckling
        className={`${isJumping ? 'jumping' : ''} ${facingLeft ? 'facing-left' : ''}`}
        style={{
          left: `${leftPosition}px`,
          transform: isJumping ? 'translateY(-150px)' : 'translateY(0)',
        }}
      />
      {/* MCQ Modal */}
      {showMCQ && currentQuestion && (
        <MCQModal questionData={currentQuestion} onAnswer={handleAnswer} />
      )}
    </div>
  );
};

export default GameBoard;
