// src/systems/InputSystem.js
import Matter from 'matter-js';

const InputSystem = (entities, { events }) => {
  let duckling = entities.duckling;

  events.forEach((event) => {
    if (event.type === 'keydown') {
      if (event.key === ' ') {
        // Jump - Apply an upward force
        Matter.Body.applyForce(duckling.body, duckling.body.position, { x: 0, y: -0.05 });
      }
      if (event.key === 'ArrowDown') {
        // Smash - Detect if over a filled block and dispatch a question
        entities.dispatch({ type: 'show-question', target: 'smash' });

        // Optional: Logic to remove the block or change its state
      }
    }
  });

  return entities;
};

export default InputSystem;
