// src/components/GameBoard.js

import React, { useState, useEffect, useRef } from 'react';
import './GameBoard.css';
import Duckling from './Duckling';
import MCQModal from './MCQModal';
import { questions } from '../data/questions';

const GameBoard = ({
  setScore,
  setLives,
  setCoins,
  score,
  lives,
  coins,
}) => {
  const gameBoardRef = useRef(null);
  const [leftPosition, setLeftPosition] = useState(50);
  const [shift, setShift] = useState(0); // New state for platform shift
  const [isJumping, setIsJumping] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showMCQ, setShowMCQ] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answeredBlocks, setAnsweredBlocks] = useState(new Set());

  const forwardSpeed = 2; // pixels per interval
  const jumpHeight = 150;
  const jumpDuration = 600;
  const blockWidth = 80;
  const totalBlocks = 20;

  const ducklingWidth = 50; // Adjust based on Duckling component's actual width
  const ducklingHeight = 50; // Adjust based on Duckling component's actual height

  const [blocks, setBlocks] = useState(() =>
    Array.from({ length: totalBlocks }).map((_, index) => ({
      type: [4, 9, 14, 19].includes(index) ? 'red' : 'blue',
      x: index * blockWidth,
      width: blockWidth,
      height: 50,
    }))
  );

  // Function to add new blocks dynamically
  const addNewBlocks = (number = 10) => {
    setBlocks((prevBlocks) => {
      const newBlocks = [];
      const startIndex = prevBlocks.length;

      for (let i = 0; i < number; i++) {
        const index = startIndex + i;
        newBlocks.push({
          type: [4, 9, 14, 19].includes(index) ? 'red' : 'blue',
          x: (startIndex + i) * blockWidth,
          width: blockWidth,
          height: 50,
        });
      }

      return [...prevBlocks, ...newBlocks];
    });
  };

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.code === 'Space' && !isJumping && !isPaused) {
        jump();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isJumping, isPaused]);

  useEffect(() => {
    if (!isPaused) {
      const gameLoop = setInterval(() => {
        setLeftPosition((prev) => {
          const potentialNewPos = prev + forwardSpeed;
          const ducklingLeft = potentialNewPos;
          const ducklingRight = ducklingLeft + ducklingWidth;

          // Determine if the duckling is approaching the shift threshold
          const ducklingPositionRelative = potentialNewPos - shift;
          const viewportWidth = gameBoardRef.current.clientWidth;
          const threshold = viewportWidth * 0.75; // 75% of viewport

          if (ducklingPositionRelative + ducklingWidth >= threshold) {
            setShift((prevShift) => prevShift + forwardSpeed);
            addNewBlocks(); // Optionally, add more blocks when shifting
          }

          // Iterate through all blocks to check for collision
          for (let i = 0; i < blocks.length; i++) {
            const block = blocks[i];
            const blockLeft = block.x;
            const blockRight = block.x + block.width;

            // Check if duckling overlaps with this block
            const isOverlapping =
              ducklingRight > (blockLeft - shift) && ducklingLeft < (blockRight - shift);

            if (
              isOverlapping &&
              block.type === 'red' &&
              !answeredBlocks.has(i)
            ) {
              triggerMCQ(i);
              return prev; // Prevent moving into the red block
            }
          }

          // Ensure the duckling doesn't move beyond the game board
          const maxLeft = gameBoardRef.current.clientWidth - ducklingWidth;
          return Math.min(potentialNewPos, maxLeft);
        });
      }, 20);

      return () => clearInterval(gameLoop);
    }
  }, [isPaused, blocks, answeredBlocks, ducklingWidth, shift]);

  useEffect(() => {
    const handleScroll = () => {
      const ducklingPositionRelative = leftPosition - shift;
      const viewportWidth = gameBoardRef.current.clientWidth;
      const threshold = viewportWidth * 0.75; // When duckling is 75% into the viewport

      // When the duckling approaches the threshold, add new blocks
      if (ducklingPositionRelative + ducklingWidth >= threshold) {
        addNewBlocks();
      }
    };

    if (!isPaused) {
      handleScroll();
    }
  }, [leftPosition, shift, isPaused, blocks]);

  const jump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setTimeout(() => setIsJumping(false), jumpDuration);
    }
  };

  const triggerMCQ = (blockIndex) => {
    setIsPaused(true);
    setShowMCQ(true);
    setCurrentQuestion(questions[Math.floor(Math.random() * questions.length)]);
    setAnsweredBlocks((prev) => new Set(prev).add(blockIndex));
  };

  const resetGame = () => {
    setLeftPosition(50);
    setShift(0); // Reset the platform shift
    setAnsweredBlocks(new Set());
    setIsPaused(false);
    setIsJumping(false);
  };

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setCoins((prevCoins) => prevCoins + 1);
      setScore((prevScore) => prevScore + 10);
    } else {
      setLives((prevLives) => prevLives - 1);
      if (lives - 1 <= 0) {
        // Game over logic here
        alert('Game Over!');
        resetGame(); // Optionally reset the game
      } else {
        resetGame(); // Reset the game state including answeredBlocks
      }
    }
    setIsPaused(false); // Resume the game
    setShowMCQ(false);  // Hide the MCQ modal
  };

  return (
    <div className="game-board" ref={gameBoardRef}>
      <div
        className="platform"
        style={{
          transform: `translateX(-${shift}px)`,
          transition: isPaused ? 'none' : 'transform 0.1s linear', // Smooth transition
        }}
      >
        {blocks.map((block, index) => (
          <div
            key={index}
            className={`block ${block.type}`}
            style={{
              left: `${block.x}px`,
              width: `${block.width}px`,
              height: `${block.height}px`,
            }}
          />
        ))}
      </div>
      <Duckling
        className={isJumping ? 'jumping' : ''}
        style={{
          left: `${leftPosition}px`,
          transform: isJumping ? `translateY(-${jumpHeight}px)` : 'translateY(0)',
          transition: `transform ${jumpDuration}ms`,
        }}
      />
      {showMCQ && currentQuestion && (
        <MCQModal questionData={currentQuestion} onAnswer={handleAnswer} />
      )}
    </div>
  );
};

export default GameBoard;
