// src/components/QuestionModal.js
import React from 'react';
import './QuestionModal.css';

const QuestionModal = ({ visible, question, options, onAnswer }) => {
  if (!visible) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{question}</h2>
        <div className="options">
          {options.map((opt, idx) => (
            <button key={idx} onClick={() => onAnswer(opt.isCorrect)}>
              {opt.text}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QuestionModal;
